mapper.select:
SELECT COUNT(*)
FROM BOARD



			AND (TITLE LIKE #{sv}
				OR REFER_NICKNAME LIKE #{sv}
				OR TYPE = ${type}
			)


			AND TITLE LIKE #{sv}


			AND REFER_NICKNAME LIKE #{sv}


			AND TYPE = #{type}

mapper.update:
UPDATE BOARD
SET VIEWS = VIEWS + 1
WHERE BID = #{bId}

UPDATE BOARD
SET TITLE=#{title}, TYPE=#{type}, CONTENT=#{content}
WHERE BID=#{bId}

mapper.select:

SELECT *
FROM BOARD



			AND (TITLE LIKE #{sv}
				OR REFER_NICKNAME LIKE #{sv}
				OR TYPE = ${type}
			)


			AND TITLE LIKE #{sv}


			AND REFER_NICKNAME LIKE #{sv}


			AND TYPE = #{type}



ORDER BY BID DESC

mapper.update:
UPDATE REPLY
SET REPORT_COUNT = REPORT_COUNT + 1,
	REPORTED_ID = #{reportedId}
WHERE RID=#{rId}
