mapper.insert:
INSERT INTO CLASSS VALUES(
	'cNo' || cno_seq.nextval, #{ornerId}, #{title},
	#{memberCount}, 1, #{level}, #{local}, #{schedule}, #{time},
	'abc', DEFAULT, #{cateId}, SYSDATE , #{comment}
)

mapper.update:
UPDATE CLASSS
SET CLASS_COUNT = CLASS_COUNT + 1
WHERE CLASS_NO = #{cNo}

mapper.select:
SELECT ORNER_ID
FROM CLASSS
WHERE CLASS_NO = #{cNo}

mapper.update:
UPDATE CLASSS
SET ORNER_ID = #{id}
WHERE CLASS_NO = #{cNo}

mapper.select:
SELECT CHANGE_NAME
FROM STORAGE
WHERE REFER_ID = #{id}

SELECT EMAIL
FROM MEMBER
WHERE MID = #{id}

SELECT CNID
FROM CLASS_NOTICE
WHERE REFER_CNO = #{cNo} AND REFER_ID = #{id} AND TITLE = #{title}

mapper.update:
UPDATE CLASS_NOTICE
SET TITLE = #{title} , NOTICE_CONTENT = #{content}
WHERE CNID = #{cnid}

UPDATE CLASS_MEMBER
SET LASTDAY = SYSDATE
WHERE CLASS_NO = #{cNo} AND ID = #{id}

mapper.delete:
DELETE
FROM CLASS_MEMBER
WHERE ID = #{id} AND CLASS_NO = #{cNo}
